import React from 'react';
import Room from  './Rooms.jsx';
import './Floors.css';

export default class Floor extends React.Component {

  constructor(props){
      super(props);
      this.state = {
        rooms: Array(24).fill(0),
        available: 24,
        booked: 0,
        occupied: 0,
        floorno: 5,
      };
  }

  handleClick(i){
    const rooms = this.state.rooms.slice();
    let available = this.state.available;
    let booked = this.state.booked;
    let occupied = this.state.occupied;
      if(rooms[i] === 0)
      {
        rooms[i]++;
        available--;
        booked++;

      }
      else if(rooms[i] === 1)
      {
        rooms[i]++;
        booked--;
        occupied++;

      }

        this.setState({
          rooms: rooms,
          available: available,
          booked: booked,
          occupied: occupied,
        });
  }


  renderRoom(i){
    return(
        <Room
         roomno = {this.state.floorno*24+i+1}
         onClick = {() => this.handleClick(i)}
         value = {this.state.rooms[i]}
         />
    );
  }

  renderPod(i){
    return(
      <div className="pod">
          {this.renderRoom(3*i+0)}
          {this.renderRoom(3*i+1)}
          {this.renderRoom(3*i+2)}
      </div>
  );
  }


