import React from 'react';

export default class Room extends React.Component {

  constructor(props) {
    super(props);
    this.handleMouseOver = this.handleMouseOver.bind(this);
    this.handleMouseOut = this.handleMouseOut.bind(this);
    this.state = {
      isHovering: false
    };
  }

  handleMouseOver() {
    this.setState(() => ({
      isHovering: true
    }));
  }

  handleMouseOut() {
    this.setState(() => ({
      isHovering: false
    }));
  }



    render()
    {
        if(this.props.value === 0){
        return (
        <button
        className="room0"
        onClick={() => this.props.onClick()}
      >
        {this.props.roomno}
      </button>
        );}

        else if(this.props.value === 1){
            return (
            <button
            className="room1"
            onClick={() => this.props.onClick()}
          >
            {this.props.roomno}
          </button>
            );}
        else {
            return (
            <button
            className="room2"
            onClick={() => this.props.onClick()}
            >
            {this.props.roomno}
            </button>
            );}
    }
}

